    22→} from './websocket-types';
    23→import { WebSocketState } from './websocket-types';
    24→
    25→const WS_URL = process.env.NEXT_PUBLIC_WS_URL || 'ws://localhost:8000/api/ws';
    26→const RECONNECT_DELAY = 3000; // 3 seconds
