    36→
    37→/**
    38→ * useWebSocket Hook
    39→ *
    40→ * Connects to the consciousness operations WebSocket stream
    41→ * and provides real-time event data to visualization components.
    42→ *
    43→ * Features:
    44→ * - Automatic reconnection on disconnect
    45→ * - Separate streams for each event type
    46→ * - Connection state tracking
    47→ * - Error handling
    48→ *
    49→ * Usage:
    50→ * const { entityActivity, thresholdCrossings, consciousnessState } = useWebSocket();
    51→ *
    52→ * Author: Iris "The Aperture"
    53→ * Backend integration: Felix "Ironhand"'s WebSocket infrastructure
    54→ */
    55→export function useWebSocket(): WebSocketStreams {
    56→  // Connection state
    57→  const [connectionState, setConnectionState] = useState<WebSocketState>(WebSocketState.CONNECTING);
    58→  const [error, setError] = useState<string | null>(null);
    59→
    60→  // Event streams (v1 legacy)
    61→  const [entityActivity, setEntityActivity] = useState<EntityActivityEvent[]>([]);
    62→  const [thresholdCrossings, setThresholdCrossings] = useState<ThresholdCrossingEvent[]>([]);
    63→  const [consciousnessState, setConsciousnessState] = useState<ConsciousnessStateEvent | null>(null);
    64→
    65→  // V2 consciousness state (frame-based)
    66→  const [v2State, setV2State] = useState<V2ConsciousnessState>({
    67→    // Frame tracking
    68→    currentFrame: null,
    69→    frameEvents: [],
    70→
    71→    // Criticality metrics
    72→    rho: null,
    73→    safety_state: null,
    74→
    75→    // Timing metrics
    76→    dt_ms: null,
    77→    interval_sched: null,
    78→    dt_used: null,
    79→
    80→    // Conservation metrics
    81→    deltaE_total: null,
    82→    conservation_error_pct: null,
    83→    energy_in: null,
    84→    energy_transferred: null,
    85→    energy_decay: null,
    86→
    87→    // Frontier metrics
    88→    active_count: null,
    89→    shadow_count: null,
    90→    diffusion_radius: null,
    91→
    92→    // Working memory and traversal
    93→    workingMemory: new Set<string>(),
    94→    recentFlips: [],
    95→    linkFlows: new Map<string, number>()
    96→  });
    97→
    98→  // Emotion coloring state
    99→  const [emotionState, setEmotionState] = useState<EmotionColoringState>({
   100→    nodeEmotions: new Map<string, EmotionMetadata>(),
   101→    linkEmotions: new Map<string, EmotionMetadata>(),
   102→    recentStrides: [],
   103→    regulationRatio: null,
   104→    resonanceRatio: null,
   105→    saturationWarnings: []
   106→  });
   107→
   108→  // Priority 4: Weight learning events
   109→  const [weightLearningEvents, setWeightLearningEvents] = useState<WeightsUpdatedTraceEvent[]>([]);
   110→
   111→  // Priority 5: Stride selection events
