   113→
   114→  // Priority 6: Phenomenology health events
   115→  const [phenomenologyMismatchEvents, setPhenomenologyMismatchEvents] = useState<PhenomenologyMismatchEvent[]>([]);
   116→  const [phenomenologyHealthEvents, setPhenomenologyHealthEvents] = useState<PhenomenologicalHealthEvent[]>([]);
   117→
   118→  // WebSocket reference (persists across renders)
   119→  const wsRef = useRef<WebSocket | null>(null);
   120→  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
   121→  const isIntentionalCloseRef = useRef(false);
   122→
   123→  // Frame throttling to prevent infinite re-render loops
   124→  const lastFrameUpdateRef = useRef<number>(0);
   125→  const FRAME_UPDATE_THROTTLE_MS = 100; // Only update every 100ms
   126→
   127→  /**
   128→   * Handle incoming WebSocket messages
   129→   */
   130→  const handleMessage = useCallback((event: MessageEvent) => {
   131→    try {
   132→      const data: WebSocketEvent = JSON.parse(event.data);
   133→
   134→      switch (data.type) {
   135→        // V1 events (legacy)
   136→        case 'entity_activity':
   137→          setEntityActivity(prev => {
   138→            const updated = [...prev, data];
   139→            return updated.slice(-MAX_ENTITY_ACTIVITIES);
   140→          });
   141→          break;
   142→
   143→        case 'threshold_crossing':
   144→          setThresholdCrossings(prev => {
   145→            const updated = [...prev, data];
   146→            return updated.slice(-MAX_THRESHOLD_CROSSINGS);
