   148→          break;
   149→
   150→        case 'consciousness_state':
   151→          setConsciousnessState(data);
   152→          break;
   153→
   154→        // V2 events (frame-based)
   155→        case 'frame.start': {
   156→          const frameEvent = data as FrameStartEvent;
   157→
   158→          // Throttle frame updates to prevent infinite re-render loop
   159→          // Frames arrive rapidly (10-60ms apart), but React needs breathing room
   160→          const now = Date.now();
   161→          if (now - lastFrameUpdateRef.current < FRAME_UPDATE_THROTTLE_MS) {
   162→            // Skip this frame update - too soon since last update
   163→            break;
   164→          }
   165→          lastFrameUpdateRef.current = now;
   166→
   167→          setV2State(prev => {
   168→            // Only update if frame actually changed (frame_id is unique per frame)
   169→            if (prev.currentFrame === frameEvent.frame_id) {
   170→              return prev; // Same frame, skip update
   171→            }
   172→
   173→            // Accumulate frame events for Priority 3 tick speed visualization
   174→            const updatedFrameEvents = [...prev.frameEvents, frameEvent].slice(-MAX_FRAME_EVENTS);
   175→
   176→            return {
   177→              ...prev,
   178→              currentFrame: frameEvent.frame_id,
   179→              frameEvents: updatedFrameEvents,
   180→
   181→              // Criticality metrics
   182→              rho: frameEvent.rho ?? prev.rho,
   183→              safety_state: frameEvent.safety_state ?? prev.safety_state,
   184→
   185→              // Timing metrics
   186→              dt_ms: frameEvent.dt_ms ?? prev.dt_ms,
   187→              interval_sched: frameEvent.interval_sched ?? prev.interval_sched,
   188→              dt_used: frameEvent.dt_used ?? prev.dt_used,
   189→
   190→              // Clear link flows at frame start (only if not already empty)
   191→              linkFlows: prev.linkFlows.size > 0 ? new Map<string, number>() : prev.linkFlows
   192→            };
   193→          });
   194→          break;
   195→        }
   196→
   197→        case 'wm.emit': {
   198→          const wmEvent = data as WmEmitEvent;
   199→          setV2State(prev => {
   200→            // Only update if working memory content changed
   201→            const newNodeIds = new Set(wmEvent.node_ids);
   202→            if (prev.workingMemory.size === newNodeIds.size &&
   203→                [...newNodeIds].every(id => prev.workingMemory.has(id))) {
   204→              return prev; // No change, return same object
   205→            }
   206→            return {
   207→              ...prev,
   208→              workingMemory: newNodeIds
   209→            };
   210→          });
   211→          break;
   212→        }
   213→
   214→        case 'node.flip': {
   215→          setV2State(prev => {
   216→            const updated = [...prev.recentFlips, data as NodeFlipEvent];
   217→            return {
   218→              ...prev,
   219→              recentFlips: updated.slice(-MAX_NODE_FLIPS)
   220→            };
   221→          });
   222→          break;
   223→        }
   224→
   225→        case 'link.flow.summary': {
   226→          const flowEvent = data as LinkFlowSummaryEvent;
   227→          setV2State(prev => {
   228→            // Guard: Check if flows array exists
   229→            if (!flowEvent.flows || !Array.isArray(flowEvent.flows)) {
   230→              console.warn('[useWebSocket] link.flow.summary event missing flows array:', flowEvent);
   231→              return prev;
   232→            }
   233→
   234→            // Check if any flow values actually changed
   235→            let hasChanges = false;
   236→            for (const flow of flowEvent.flows) {
   237→              if (prev.linkFlows.get(flow.link_id) !== flow.count) {
   238→                hasChanges = true;
   239→                break;
   240→              }
   241→            }
   242→
   243→            if (!hasChanges) {
   244→              return prev; // No changes, return same object
   245→            }
   246→
   247→            const newFlows = new Map(prev.linkFlows);
   248→            flowEvent.flows.forEach(flow => {
   249→              newFlows.set(flow.link_id, flow.count);
   250→            });
   251→            return {
   252→              ...prev,
   253→              linkFlows: newFlows
   254→            };
   255→          });
   256→          break;
   257→        }
   258→
   259→        case 'frame.end': {
   260→          const frameEndEvent = data as FrameEndEvent;
   261→          setV2State(prev => {
   262→            // Check if any values actually changed
   263→            if (
   264→              prev.deltaE_total === frameEndEvent.deltaE_total &&
   265→              prev.conservation_error_pct === frameEndEvent.conservation_error_pct &&
   266→              prev.energy_in === frameEndEvent.energy_in &&
   267→              prev.energy_transferred === frameEndEvent.energy_transferred &&
   268→              prev.energy_decay === frameEndEvent.energy_decay &&
   269→              prev.active_count === frameEndEvent.active_count &&
   270→              prev.shadow_count === frameEndEvent.shadow_count &&
   271→              prev.diffusion_radius === frameEndEvent.diffusion_radius
   272→            ) {
   273→              return prev; // No changes, skip update
   274→            }
   275→
   276→            return {
   277→              ...prev,
   278→
   279→              // Conservation metrics
   280→              deltaE_total: frameEndEvent.deltaE_total ?? prev.deltaE_total,
   281→              conservation_error_pct: frameEndEvent.conservation_error_pct ?? prev.conservation_error_pct,
   282→              energy_in: frameEndEvent.energy_in ?? prev.energy_in,
   283→              energy_transferred: frameEndEvent.energy_transferred ?? prev.energy_transferred,
   284→              energy_decay: frameEndEvent.energy_decay ?? prev.energy_decay,
   285→
   286→              // Frontier metrics
   287→              active_count: frameEndEvent.active_count ?? prev.active_count,
   288→              shadow_count: frameEndEvent.shadow_count ?? prev.shadow_count,
   289→              diffusion_radius: frameEndEvent.diffusion_radius ?? prev.diffusion_radius
   290→            };
   291→          });
   292→          break;
   293→        }
   294→
   295→        // Emotion coloring events
   296→        case 'node.emotion.update': {
   297→          const emotionEvent = data as NodeEmotionUpdateEvent;
   298→          setEmotionState(prev => {
   299→            // Create emotion metadata from event
   300→            const metadata: EmotionMetadata = {
   301→              magnitude: emotionEvent.emotion_magnitude,
   302→              axes: emotionEvent.top_axes,
   303→              lastUpdated: Date.parse(emotionEvent.timestamp),
   304→              displayedMagnitude: prev.nodeEmotions.get(emotionEvent.node_id)?.displayedMagnitude ?? emotionEvent.emotion_magnitude
   305→            };
   306→
   307→            // Update node emotions map
   308→            const newNodeEmotions = new Map(prev.nodeEmotions);
   309→            newNodeEmotions.set(emotionEvent.node_id, metadata);
   310→
   311→            // Check for saturation warnings
   312→            const saturationWarnings: string[] = [];
   313→            for (const [nodeId, meta] of newNodeEmotions.entries()) {
   314→              if (meta.magnitude > SATURATION_THRESHOLD) {
   315→                saturationWarnings.push(nodeId);
   316→              }
   317→            }
   318→
   319→            return {
   320→              ...prev,
   321→              nodeEmotions: newNodeEmotions,
   322→              saturationWarnings
   323→            };
   324→          });
   325→          break;
   326→        }
   327→
   328→        case 'link.emotion.update': {
   329→          const emotionEvent = data as LinkEmotionUpdateEvent;
   330→          setEmotionState(prev => {
   331→            // Create emotion metadata from event
   332→            const metadata: EmotionMetadata = {
   333→              magnitude: emotionEvent.emotion_magnitude,
   334→              axes: emotionEvent.top_axes,
   335→              lastUpdated: Date.parse(emotionEvent.timestamp),
   336→              displayedMagnitude: prev.linkEmotions.get(emotionEvent.link_id)?.displayedMagnitude ?? emotionEvent.emotion_magnitude
   337→            };
   338→
   339→            // Update link emotions map
   340→            const newLinkEmotions = new Map(prev.linkEmotions);
   341→            newLinkEmotions.set(emotionEvent.link_id, metadata);
   342→
   343→            return {
   344→              ...prev,
   345→              linkEmotions: newLinkEmotions
   346→            };
   347→          });
   348→          break;
   349→        }
   350→
   351→        case 'stride.exec': {
   352→          const strideEvent = data as StrideExecEvent;
   353→          setEmotionState(prev => {
   354→            // Add to recent strides for attribution
   355→            const updated = [...prev.recentStrides, strideEvent];
   356→            const recentStrides = updated.slice(-MAX_RECENT_STRIDES);
   357→
   358→            // Calculate regulation vs resonance ratios from recent strides
   359→            let compCount = 0;
   360→            let resCount = 0;
   361→
   362→            for (const stride of recentStrides) {
   363→              // Count as complementarity-driven if comp multiplier reduced cost more than resonance
   364→              if (stride.comp_multiplier < stride.resonance_multiplier) {
   365→                compCount++;
   366→              } else if (stride.resonance_multiplier < stride.comp_multiplier) {
   367→                resCount++;
   368→              }
   369→              // If equal, don't count either (neutral)
   370→            }
   371→
   372→            const total = compCount + resCount;
   373→            const regulationRatio = total > 0 ? compCount / total : null;
   374→            const resonanceRatio = total > 0 ? resCount / total : null;
   375→
   376→            return {
   377→              ...prev,
   378→              recentStrides,
   379→              regulationRatio,
   380→              resonanceRatio
   381→            };
   382→          });
   383→          break;
   384→        }
   385→
   386→        // Internal consciousness engine events (no UI updates needed)
   387→        case 'criticality.state':
