   469→  /**
   470→   * Connect to WebSocket
   471→   */
   472→  const connect = useCallback(() => {
   473→    // Don't reconnect if intentionally closed
   474→    if (isIntentionalCloseRef.current) {
   475→      return;
   476→    }
   477→
   478→    try {
   479→      console.log('[WebSocket] Connecting to:', WS_URL);
   480→      setConnectionState(WebSocketState.CONNECTING);
   481→      setError(null);
   482→
   483→      const ws = new WebSocket(WS_URL);
   484→      wsRef.current = ws;
   485→
   486→      ws.onopen = () => {
   487→        console.log('[WebSocket] Connected');
   488→        setConnectionState(WebSocketState.CONNECTED);
   489→        setError(null);
   490→      };
   491→
   492→      ws.onmessage = handleMessage;
   493→
   494→      ws.onerror = (event) => {
   495→        // WebSocket unavailable is expected degraded state, not error
   496→        console.log('[WebSocket] Connection unavailable - will retry');
   497→        setConnectionState(WebSocketState.ERROR);
   498→        setError('WebSocket connection error');
   499→      };
   500→
   501→      ws.onclose = (event) => {
   502→        console.log('[WebSocket] Disconnected:', event.code, event.reason);
   503→        setConnectionState(WebSocketState.DISCONNECTED);
   504→
   505→        // Attempt reconnection if not intentionally closed
   506→        if (!isIntentionalCloseRef.current) {
   507→          console.log(`[WebSocket] Reconnecting in ${RECONNECT_DELAY}ms...`);
   508→          reconnectTimeoutRef.current = setTimeout(() => {
   509→            connect();
   510→          }, RECONNECT_DELAY);
   511→        }
   512→      };
   513→    } catch (err) {
   514→      console.error('[WebSocket] Connection error:', err);
   515→      setConnectionState(WebSocketState.ERROR);
   516→      setError(err instanceof Error ? err.message : 'Unknown connection error');
   517→
   518→      // Attempt reconnection
   519→      if (!isIntentionalCloseRef.current) {
   520→        reconnectTimeoutRef.current = setTimeout(() => {
   521→          connect();
   522→        }, RECONNECT_DELAY);
   523→      }
   524→    }
   525→  }, [handleMessage]);
   526→
   527→  /**
   528→   * Disconnect WebSocket
   529→   */
   530→  const disconnect = useCallback(() => {
   531→    isIntentionalCloseRef.current = true;
   532→
   533→    // Clear reconnection timeout
   534→    if (reconnectTimeoutRef.current) {
   535→      clearTimeout(reconnectTimeoutRef.current);
   536→      reconnectTimeoutRef.current = null;
   537→    }
   538→
   539→    // Close WebSocket connection
   540→    if (wsRef.current) {
   541→      wsRef.current.close();
   542→      wsRef.current = null;
   543→    }
   544→
   545→    setConnectionState(WebSocketState.DISCONNECTED);
   546→  }, []);
   547→
   548→  /**
   549→   * Initialize WebSocket connection on mount
   550→   * Cleanup on unmount
   551→   */
   552→  useEffect(() => {
   553→    isIntentionalCloseRef.current = false;
   554→    connect();
   555→
   556→    // Cleanup on unmount
   557→    return () => {
   558→      disconnect();
   559→    };
   560→  }, [connect, disconnect]);
   561→
   562→  return {
   563→    // V1 events
   564→    entityActivity,
   565→    thresholdCrossings,
   566→    consciousnessState,
   567→
   568→    // V2 events
   569→    v2State,
   570→
   571→    // Emotion coloring
   572→    emotionState,
   573→
   574→    // Priority 4: Weight learning
   575→    weightLearningEvents,
   576→
   577→    // Priority 5: Stride selection
   578→    strideSelectionEvents,
   579→
   580→    // Priority 6: Phenomenology health
   581→    phenomenologyMismatchEvents,
   582→    phenomenologyHealthEvents,
   583→
   584→    // Connection
   585→    connectionState,
   586→    error
   587→  };
   588→}
