    41→      return NextResponse.json(
    42→        {
    43→          error: 'Failed to fetch citizen status',
    44→          details: errorText,
    45→          backend_status: response.status
    46→        },
    47→        { status: response.status }
    48→      );
    49→    }
    50→
    51→    const data = await response.json();
    52→    return NextResponse.json(data);
    53→
    54→  } catch (error) {
    55→    console.error('[API] Error fetching citizen status:', error);
