    34→/**
    35→ * useCitizens - Dynamic citizen discovery from FalkorDB
    36→ *
    37→ * Fetches available citizens from the graph database (source of truth)
    38→ * instead of using hardcoded arrays. Polls every 10 seconds to detect
    39→ * new citizens automatically.
    40→ *
    41→ * Architecture:
    42→ * 1. Fetch /api/graphs to get all citizens from FalkorDB
    43→ * 2. Transform into Citizen format for CitizenMonitor
    44→ * 3. CitizenMonitor polls /api/citizen/{id}/status for detailed status
    45→ *
    46→ * Author: Iris "The Aperture"
    47→ * Date: 2025-10-19
    48→ * Purpose: Make new citizens appear automatically when their graph is created
    49→ */
    50→export function useCitizens() {
    51→  const [citizens, setCitizens] = useState<Citizen[]>([]);
    52→  const [loading, setLoading] = useState(true);
    53→  const [error, setError] = useState<string | null>(null);
    54→
    55→  useEffect(() => {
    56→    const fetchCitizens = async () => {
    57→      try {
    58→        const response = await fetch('/api/graphs');
    59→
    60→        if (!response.ok) {
    61→          throw new Error(`Failed to fetch graphs: ${response.status}`);
    62→        }
    63→
    64→        const data: GraphsResponse = await response.json();
    65→
    66→        // Handle backend unavailable gracefully (guardian restarts, etc.)
    67→        if ('error' in data && data.citizens.length === 0) {
    68→          console.warn('[useCitizens] Backend temporarily unavailable, will retry...');
    69→          // Don't set error state - polling will retry in 10 seconds
    70→          return;
    71→        }
    72→
    73→        // Transform citizens from graph API to Citizen interface
    74→        const transformedCitizens: Citizen[] = data.citizens.map(citizen => {
    75→          // Extract citizen_id from graph_id (e.g., "citizen_felix" -> "felix")
    76→          const citizenId = citizen.id.replace('citizen_', '');
    77→
    78→          return {
    79→            id: citizenId,
    80→            name: citizen.name,
    81→            // Default state - will be updated by CitizenMonitor's status polling
    82→            state: 'active',
    83→            lastThought: 'Initializing...',
    84→            subentities: [],
    85→            lastUpdate: 'Just now',
    86→            // Default values - CitizenMonitor's API polling will override these
    87→            tickInterval: 200,
    88→            energyTotal: 100,
    89→            energyUsed: 0,
    90→          };
    91→        });
    92→
    93→        // Add N2 (organizational) graph as special "collective" citizen
    94→        if (data.organizations.length > 0) {
    95→          const orgGraph = data.organizations.find(org =>
    96→            org.id === 'org_mind_protocol'
    97→          );
    98→
    99→          if (orgGraph) {
   100→            transformedCitizens.unshift({
   101→              id: 'mind_protocol',
   102→              name: 'Mind Protocol',
   103→              state: 'active',
   104→              lastThought: 'Collective intelligence emerging from citizen collaboration',
   105→              subentities: [],
   106→              lastUpdate: '1s ago',
   107→              tickInterval: 200,
   108→              energyTotal: 150,
   109→              energyUsed: 0,
   110→            });
   111→          }
   112→        }
   113→
   114→        setCitizens(transformedCitizens);
   115→        setError(null);
   116→        setLoading(false);
   117→
   118→      } catch (err) {
   119→        console.error('[useCitizens] Error fetching citizens:', err);
   120→        setError(err instanceof Error ? err.message : 'Unknown error');
   121→        setLoading(false);
   122→
   123→        // Fall back to empty array instead of crashing
   124→        setCitizens([]);
   125→      }
   126→    };
   127→
   128→    // Initial fetch
   129→    fetchCitizens();
   130→
   131→    // Poll every 10 seconds to detect new citizens
   132→    const interval = setInterval(fetchCitizens, 10000);
   133→
   134→    return () => clearInterval(interval);
   135→  }, []);
   136→
   137→  return { citizens, loading, error };
   138→}
