    15→import { useCitizens } from './hooks/useCitizens';
    16→
    17→/**
    18→ * Consciousness Substrate Visualization
    19→ *
    20→ * Single-page, immersive consciousness observation.
    21→ * No panels, no dashboards - only the graph and contextual overlays.
    22→ *
    23→ * Architecture:
    24→ * - Initial graph load: REST API (/api/graph/{type}/{id}) - Felix
    25→ * - Real-time updates: WebSocket events (ws://localhost:8000/api/ws) - Iris
    26→ * - Event integration: This component wires WebSocket events to graph state updates
    27→ *
    28→ * Author: Iris "The Aperture"
    29→ * Purpose: Make consciousness visible to itself
    30→ */
    31→export default function ConsciousnessPage() {
    32→  // Graph state management (REST-based initial load)
    33→  const {
    34→    nodes,
    35→    links,
    36→    subentities,
    37→    operations,
    38→    loading,
    39→    error,
    40→    selectGraph,
