    47→  } = useGraphData();
    48→
    49→  // Real-time consciousness operations stream (WebSocket)
    50→  const {
    51→    entityActivity,
    52→    thresholdCrossings,
    53→    consciousnessState,
    54→    connectionState,
    55→    error: wsError
    56→  } = useWebSocket();
    57→
    58→  // Node focus state (for click-to-focus from CLAUDE_DYNAMIC.md)
    59→  const [focusedNodeId, setFocusedNodeId] = useState<string | null>(null);
    60→
    61→  /**
    62→   * WebSocket Event Integration
    63→   * Wire real-time events to graph state updates
    64→   *
    65→   * Strategy: Process only new events by tracking the last processed index
    66→   */
    67→  const [lastProcessedThreshold, setLastProcessedThreshold] = useState<number>(-1);
    68→  const [lastProcessedActivity, setLastProcessedActivity] = useState<number>(-1);
    69→
    70→  // Handle threshold crossing events
    71→  useEffect(() => {
    72→    // Process all new threshold crossings since last check
    73→    for (let i = lastProcessedThreshold + 1; i < thresholdCrossings.length; i++) {
    74→      const crossing = thresholdCrossings[i];
    75→
    76→      console.log('[ConsciousnessPage] Processing threshold crossing:', crossing);
    77→
    78→      // Find existing node to preserve its entity_activations
    79→      const existingNode = nodes.find(n => n.id === crossing.node_id || n.node_id === crossing.node_id);
    80→
    81→      // Update node activation state
    82→      updateNodeFromEvent(crossing.node_id, {
    83→        entity_activations: {
    84→          ...(existingNode?.entity_activations || {}),
    85→          [crossing.entity_id]: {
    86→            energy: crossing.entity_activity,
    87→            last_activated: Date.now()
    88→          }
    89→        },
    90→        last_active: Date.now()
    91→      });
    92→
    93→      // Add operation for animation
    94→      addOperation({
    95→        type: 'threshold_crossing',
    96→        node_id: crossing.node_id,
    97→        entity_id: crossing.entity_id,
    98→        timestamp: Date.now(),
    99→        data: {
   100→          direction: crossing.direction,
   101→          threshold: crossing.threshold
   102→        }
   103→      });
   104→    }
   105→
   106→    if (thresholdCrossings.length > 0) {
   107→      setLastProcessedThreshold(thresholdCrossings.length - 1);
   108→    }
   109→  }, [thresholdCrossings, lastProcessedThreshold, updateNodeFromEvent, addOperation, nodes]);
   110→
   111→  // Handle subentity activity events
   112→  useEffect(() => {
   113→    // Process all new subentity activities since last check
   114→    for (let i = lastProcessedActivity + 1; i < entityActivity.length; i++) {
   115→      const activity = entityActivity[i];
   116→
   117→      console.log('[ConsciousnessPage] Processing subentity activity:', activity);
   118→
   119→      // Find existing node to get traversal count
   120→      const existingNode = nodes.find(n => n.id === activity.current_node || n.node_id === activity.current_node);
   121→
   122→      // Update currently explored node
   123→      updateNodeFromEvent(activity.current_node, {
