   167→  const { citizens, loading: citizensLoading, error: citizensError } = useCitizens();
   168→
   169→  // Handle citizen selection from sidebar (switches graph)
   170→  const handleSelectCitizen = useCallback((citizenId: string) => {
   171→    // Determine graph type and ID
   172→    if (citizenId === 'mind_protocol') {
   173→      selectGraph('organization', 'org_mind_protocol');
   174→    } else {
   175→      selectGraph('citizen', `citizen_${citizenId}`);
   176→    }
   177→  }, [selectGraph]);
