   248→```tsx
   249→interface ConsciousnessActivityPath {
   250→  id: string;
   251→  entityId: string;
   252→  path: {x: number, y: number}[];
   253→  type: 'processing' | 'communication' | 'observation' | 'memory_access';
   254→  startTime: Date;
   255→  endTime?: Date;
   256→  throughput?: number; // Hz or events/second
   257→}
   258→
   259→// Mind Harbor Path Rendering Component
   260→export function ConsciousnessPathsLayer({
   261→  paths,
   262→  hoveredEntityId,
   263→  selectedEntityId,
   264→  canvasWidth,
   265→  canvasHeight
   266→}: PathsLayerProps) {
   267→  const [animatingPathId, setAnimatingPathId] = useState<string | null>(null);
   268→  const [pathDashOffsets, setPathDashOffsets] = useState<Record<string, number>>({});
   269→
   270→  // Get path color based on consciousness activity type
   271→  const getPathColor = (path: ConsciousnessActivityPath): string => {
   272→    switch (path.type) {
   273→      case 'processing':
   274→        return '#00d9ff'; // Teal wireframe
   275→      case 'communication':
   276→        return '#00ff88'; // Emerald wireframe
   277→      case 'observation':
   278→        return '#ffd700'; // Gold wireframe
   279→      case 'memory_access':
   280→        return '#b19cd9'; // Violet wireframe
   281→      default:
   282→        return '#c0c0c0'; // Silver wireframe
   283→    }
   284→  };
   285→
   286→  // Filter paths by visibility rules
   287→  const visiblePaths = paths.filter(path => {
   288→    // Always show high-throughput paths (like merchant galleys)
   289→    if (path.throughput && path.throughput > 10) return true;
   290→
   291→    // Show paths for hovered or selected subentity
   292→    if (path.entityId === hoveredEntityId || path.entityId === selectedEntityId) {
   293→      return true;
   294→    }
   295→
   296→    return false;
   297→  });
   298→
   299→  return (
   300→    <svg
   301→      className="absolute inset-0 pointer-events-none"
   302→      style={{
   303→        zIndex: 10,
   304→        width: canvasWidth,
   305→        height: canvasHeight,
   306→        overflow: 'visible'
   307→      }}
   308→    >
   309→      {visiblePaths.map(path => {
   310→        if (path.path.length < 2) return null;
   311→
   312→        const pointsString = path.path
   313→          .map(p => `${p.x},${p.y}`)
   314→          .join(' ');
   315→
   316→        const isAnimating = path.id === animatingPathId;
   317→        const strokeWidth = path.throughput && path.throughput > 10 ? 4 : 2;
   318→        const strokeOpacity = path.entityId === selectedEntityId ? 0.9 : 0.6;
   319→
   320→        return (
