   327→              strokeWidth={strokeWidth}
   328→              strokeOpacity={strokeOpacity}
   329→              strokeLinecap="round"
   330→              strokeLinejoin="round"
   331→              filter="url(#wireframe-glow)"
   332→              style={{
   333→                strokeDasharray: isAnimating ? pathDashOffsets[path.id] : undefined,
   334→                strokeDashoffset: isAnimating ? pathDashOffsets[path.id] : undefined
   335→              }}
   336→            />
   337→
   338→            {/* Start/end point markers */}
   339→            <circle
   340→              cx={path.path[0].x}
   341→              cy={path.path[0].y}
   342→              r="3"
   343→              fill={getPathColor(path)}
   344→              opacity="0.8"
   345→            />
   346→            <circle
   347→              cx={path.path[path.path.length - 1].x}
   348→              cy={path.path[path.path.length - 1].y}
   349→              r="3"
   350→              fill={getPathColor(path)}
   351→              opacity="0.8"
   352→            />
   353→          </g>
   354→        );
   355→      })}
   356→
   357→      {/* SVG filter for wireframe glow */}
