   339→function getNodeSize(node: Node): number {
   340→  // Use computed weight from node or fallback to calculation
   341→  const weight = node.weight || computeNodeWeight(node);
   342→  // Expanded range: 20px (min) to 48px (max weight)
   343→  return Math.max(20, 16 + weight * 32);
   344→}
   345→
   346→function computeNodeWeight(node: Node): number {
   347→  const energy = node.energy || 0;
   348→  const confidence = node.confidence || 0.5;
   349→  const traversalCount = node.traversal_count || 0;
   350→  const normalizedTraversals = Math.min(1.0, Math.log10(traversalCount + 1) / 2);
   351→  return (energy * 0.4) + (confidence * 0.3) + (normalizedTraversals * 0.3);
   352→}
