   354→function getNodeGlow(node: Node): string {
   355→  const now = Date.now();
   356→
   357→  // Priority: Show only the most important glow (performance optimization)
   358→
   359→  // 1. NEWEST CREATED (last 30 seconds) - Blue pulse (highest priority)
   360→  if (node.created_at) {
   361→    const age = now - node.created_at;
   362→    if (age < 30000) { // 30 seconds
   363→      const intensity = 1 - (age / 30000);
   364→      const glowSize = 8 + (intensity * 10);
   365→      return `drop-shadow(0 0 ${glowSize}px rgba(59, 130, 246, ${intensity * 0.9}))`;
   366→    }
   367→  }
   368→
   369→  // 2. RECENTLY REINFORCED/DE-REINFORCED (last 2 minutes)
   370→  if (node.last_modified && node.reinforcement_weight !== undefined) {
   371→    const age = now - node.last_modified;
   372→    if (age < 120000) { // 2 minutes
   373→      const intensity = 1 - (age / 120000);
   374→      const glowSize = 6 + (intensity * 8);
   375→
   376→      if (node.reinforcement_weight > 0.05) {
   377→        // GREEN for positive reinforcement
   378→        return `drop-shadow(0 0 ${glowSize}px rgba(34, 197, 94, ${intensity * 0.8}))`;
   379→      } else if (node.reinforcement_weight < -0.05) {
   380→        // RED for de-reinforcement
   381→        return `drop-shadow(0 0 ${glowSize}px rgba(239, 68, 68, ${intensity * 0.8}))`;
   382→      }
   383→    }
   384→  }
   385→
   386→  // 3. RECENTLY TRAVERSED (last 2 minutes) - Yellow/green (lowest priority)
   387→  if (node.last_active) {
   388→    const age = now - node.last_active;
   389→    if (age < 120000) { // 2 minutes
   390→      const intensity = 1 - (age / 120000);
   391→      const glowSize = 5 + (intensity * 6);
   392→      return `drop-shadow(0 0 ${glowSize}px rgba(94, 252, 130, ${intensity * 0.6}))`;
   393→    }
   394→  }
   395→
   396→  return 'none';
   397→}
