   410→function getLinkTypeColor(type: string): string {
   411→  const COLORS: Record<string, string> = {
   412→    'JUSTIFIES': '#ef4444',
   413→    'BUILDS_TOWARD': '#3b82f6',
   414→    'ENABLES': '#22c55e',
   415→    'USES': '#8b5cf6',
   416→    'default': '#666'
   417→  };
