   421→function getValenceColor(valence: number | undefined): string {
   422→  if (valence === null || valence === undefined) return '#64748b';
   423→
   424→  const normalized = (valence + 1.0) / 2.0;
   425→
   426→  if (normalized < 0.5) {
   427→    const t = normalized * 2;
   428→    return d3.interpolateRgb('#ef4444', '#94a3b8')(t); // Red to gray
   429→  } else {
   430→    const t = (normalized - 0.5) * 2;
   431→    return d3.interpolateRgb('#94a3b8', '#22c55e')(t); // Gray to green
   432→  }
   433→}
