   452→// ============================================================================
   453→// Temporal Force (Timeline Layout)
   454→// ============================================================================
   455→
   456→function forceTemporalX(width: number) {
   457→  let nodes: Node[];
   458→
   459→  function force(alpha: number) {
   460→    if (!nodes) return;
   461→
   462→    const now = Date.now();
   463→    // Find oldest and newest timestamps
   464→    const timestamps = nodes
   465→      .map(n => n.last_active || n.created_at || 0)
   466→      .filter(t => t > 0);
   467→
   468→    if (timestamps.length === 0) return;
   469→
   470→    const minTime = Math.min(...timestamps);
   471→    const maxTime = Math.max(...timestamps);
   472→    const timeRange = maxTime - minTime;
   473→
   474→    // FALLBACK: If timestamps are uniform (seed data), disable temporal force
   475→    // Range less than 1 hour = likely bulk-imported seed data
   476→    if (timeRange < 3600000) {
   477→      // console.log('[Temporal Force] Timestamps too uniform, skipping');
   478→      return; // Let other forces handle layout
   479→    }
   480→
   481→    // Pull nodes left-to-right based on temporal position
   482→    nodes.forEach((node: any) => {
   483→      const nodeTime = node.last_active || node.created_at || minTime;
   484→      const timePos = (nodeTime - minTime) / timeRange; // 0 (old) to 1 (new)
   485→
   486→      // Target X position: old on left (20% width), new on right (80% width)
   487→      const targetX = width * 0.2 + (timePos * width * 0.6);
   488→
   489→      // Apply gentle force toward target position
   490→      const dx = targetX - node.x;
   491→      node.vx += dx * alpha * 0.15; // Gentle 15% strength
   492→    });
   493→  }
   494→
   495→  force.initialize = function(_: any) {
   496→    nodes = _;
   497→  };
   498→
   499→  return force;
   500→}
   501→
   502→function forceValenceY(height: number) {
   503→  let nodes: Node[];
   504→
   505→  function force(alpha: number) {
   506→    if (!nodes) return;
   507→
   508→    // Pull nodes up/down based on emotional valence
   509→    nodes.forEach((node: any) => {
   510→      const valence = computeNodeValence(node);
   511→
   512→      // Target Y position: negative at bottom (80%), positive at top (20%)
   513→      // Valence ranges from -1 (bottom) to +1 (top)
   514→      const valencePos = (valence + 1) / 2; // Normalize to 0-1
   515→      const targetY = height * 0.8 - (valencePos * height * 0.6); // Invert: positive up
   516→
   517→      // Apply gentle force toward target position
   518→      const dy = targetY - node.y;
   519→      node.vy += dy * alpha * 0.12; // Slightly weaker than temporal (12% vs 15%)
   520→    });
   521→  }
   522→
   523→  force.initialize = function(_: any) {
   524→    nodes = _;
   525→  };
   526→
   527→  return force;
   528→}
   529→
   530→function computeNodeValence(node: Node): number {
   531→  // Aggregate valence from node properties
   532→  let valence = 0;
   533→  let count = 0;
   534→
   535→  // Check if node has entity_activations with valence data
   536→  if (node.entity_activations) {
   537→    Object.values(node.entity_activations).forEach((activation: any) => {
   538→      if (activation.valence !== undefined) {
   539→        valence += activation.valence;
   540→        count++;
   541→      }
   542→    });
   543→  }
   544→
   545→  // Node type-based valence hints (adds diversity even without runtime data)
   546→  const nodeType = node.node_type || '';
   547→  if (nodeType === 'Best_Practice' || nodeType === 'Realization' || nodeType === 'Personal_Goal') {
   548→    valence += 0.3;
   549→    count++;
   550→  } else if (nodeType === 'Anti_Pattern' || nodeType === 'Wound' || nodeType === 'Risk') {
   551→    valence -= 0.3;
   552→    count++;
   553→  } else if (nodeType === 'Trigger' || nodeType === 'Coping_Mechanism') {
   554→    valence -= 0.15;
   555→    count++;
   556→  }
   557→
   558→  // Confidence as slight positive bias (higher confidence = slight upward pull)
   559→  if (node.confidence !== undefined && node.confidence !== null) {
   560→    valence += (node.confidence - 0.5) * 0.2; // -0.1 to +0.1
   561→    count++;
   562→  }
   563→
   564→  // Reinforcement weight affects valence
   565→  if (node.reinforcement_weight !== undefined) {
   566→    valence += node.reinforcement_weight * 0.5; // Scale reinforcement to valence
   567→    count++;
   568→  }
   569→
   570→  // FALLBACK: Even with no data, use small random jitter to prevent perfect stacking
   571→  if (count === 0) {
   572→    // Use node ID as seed for consistent but distributed positioning
   573→    const seed = parseInt(node.id, 36) || 0;
   574→    return ((seed % 100) / 100 - 0.5) * 0.4; // -0.2 to +0.2 range
   575→  }
   576→
   577→  return Math.max(-1, Math.min(1, valence / count));
   578→}
   579→
   580→// ============================================================================
   581→// Drag Behavior
   582→// ============================================================================
   583→
   584→function drag(simulation: d3.Simulation<any, undefined>) {
   585→  function dragstarted(event: any) {
   586→    if (!event.active) simulation.alphaTarget(0.3).restart();
   587→    event.subject.fx = event.subject.x;
   588→    event.subject.fy = event.subject.y;
   589→  }
   590→
   591→  function dragged(event: any) {
   592→    event.subject.fx = event.x;
   593→    event.subject.fy = event.y;
   594→  }
   595→
   596→  function dragended(event: any) {
   597→    if (!event.active) simulation.alphaTarget(0);
   598→    event.subject.fx = null;
   599→    event.subject.fy = null;
   600→  }
   601→
   602→  return d3.drag()
   603→    .on('start', dragstarted)
   604→    .on('drag', dragged)
   605→    .on('end', dragended);
   606→}
