// Render nodes (parchment-backed emoji groups with Venice aesthetic)
const nodeGroups = g.append('g')
  .selectAll('g.node')
  .data(nodes)
  .join('g')
  .attr('class', 'node')
  .style('cursor', 'pointer')
  .style('user-select', 'none')
  .style('pointer-events', 'all')
  .call(drag(simulation) as any)
  .on('click', (event, d) => {
    event.stopPropagation();
    const customEvent = new CustomEvent('node:click', { detail: { node: d, event } });
    window.dispatchEvent(customEvent);
  })
  .on('mouseenter', (event, d) => {
    const customEvent = new CustomEvent('node:hover', { detail: { node: d, event } });
    window.dispatchEvent(customEvent);
  })
  .on('mouseleave', () => {
    const customEvent = new CustomEvent('node:leave');
    window.dispatchEvent(customEvent);
  });

// Add parchment background circles (captures pointer events)
nodeGroups.append('circle')
  .attr('r', d => getNodeSize(d) / 2)
  .attr('fill', '#f5e7c1') // Parchment base color
  .attr('stroke', '#e8e2d5') // Parchment border
  .attr('stroke-width', 1.5)
  .style('filter', 'url(#parchment-texture) drop-shadow(0 2px 4px rgba(0,0,0,0.2))');

// Add emoji text with wireframe glow
const nodeElements = nodeGroups.append('text')
  .style('font-family', '"Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji", sans-serif')
  .style('text-anchor', 'middle')
  .style('dominant-baseline', 'central')
  .style('pointer-events', 'none')
  .attr('font-size', d => getNodeSize(d) * 0.5)
  .text(d => getNodeEmoji(d))
  .style('filter', 'url(#wireframe-glow)');

// Get circle backgrounds for applying activity glows and gold shimmer
const circleBackgrounds = nodeGroups.select('circle');

// Update glows and gold shimmer only once per second (performance optimization)
const updateNodeEffects = () => {
  circleBackgrounds.each(function(d: any) {
    const circle = d3.select(this);
    const activityGlow = getNodeGlow(d);
    const hasGoldEnergy = shouldApplyGoldShimmer(d);

    // Build filter string: parchment texture + activity glow + gold shimmer
    let filterStr = 'url(#parchment-texture)';

    if (hasGoldEnergy) {
      filterStr += ' url(#gold-shimmer)';
    }

    filterStr += ` drop-shadow(0 2px 4px rgba(0,0,0,0.2))`;

    if (activityGlow !== 'none') {
      // Extract drop-shadow from activity glow and append
      filterStr += ` ${activityGlow}`;
    }

    circle.style('filter', filterStr);
  });
};
updateNodeEffects(); // Initial update
const effectInterval = setInterval(updateNodeEffects, 1000); // Update every second

// Simulation tick - position groups instead of individual elements
simulation.on('tick', () => {
  linkElements
    .attr('x1', (d: any) => d.source.x)
    .attr('y1', (d: any) => d.source.y)
    .attr('x2', (d: any) => d.target.x)
    .attr('y2', (d: any) => d.target.y);

  nodeGroups
    .attr('transform', (d: any) => `translate(${d.x},${d.y})`);
});

