function getNodeGlow(node: Node): string {
  const now = Date.now();

  // Priority: Show only the most important glow (performance optimization)

  // 1. NEWEST CREATED (last 30 seconds) - Blue pulse (highest priority)
  if (node.created_at) {
    const age = now - node.created_at;
    if (age < 30000) { // 30 seconds
      const intensity = 1 - (age / 30000);
      const glowSize = 8 + (intensity * 10);
      return `drop-shadow(0 0 ${glowSize}px rgba(59, 130, 246, ${intensity * 0.9}))`;
    }
  }

