     3â†’import { useState, useEffect, useRef, useCallback } from 'react';
     4â†’import type {
     5â†’  WebSocketEvent,
     6â†’  EntityActivityEvent,
     7â†’  ThresholdCrossingEvent,
     8â†’  ConsciousnessStateEvent,
     9â†’  WebSocketStreams
    10â†’} from './websocket-types';
    11â†’import { WebSocketState } from './websocket-types';
    12â†’
    13â†’const WS_URL = process.env.NEXT_PUBLIC_WS_URL || 'ws://localhost:8000/api/ws';
    14â†’const RECONNECT_DELAY = 3000; // 3 seconds
    15â†’const MAX_ENTITY_ACTIVITIES = 100; // Keep last 100 subentity activities
    16â†’const MAX_THRESHOLD_CROSSINGS = 50; // Keep last 50 threshold crossings
    17â†’
    18â†’/**
    19â†’ * useWebSocket Hook
    20â†’ *
    21â†’ * Connects to the consciousness operations WebSocket stream
    22â†’ * and provides real-time event data to visualization components.
    23â†’ *
    24â†’ * Features:
    25â†’ * - Automatic reconnection on disconnect
    26â†’ * - Separate streams for each event type
    27â†’ * - Connection state tracking
    28â†’ * - Error handling
    29â†’ *
    30â†’ * Usage:
    31â†’ * const { entityActivity, thresholdCrossings, consciousnessState } = useWebSocket();
    32â†’ *
    33â†’ * Author: Iris "The Aperture"
    34â†’ * Backend integration: Felix "Ironhand"'s WebSocket infrastructure
    35â†’ */
    36â†’export function useWebSocket(): WebSocketStreams {
    37â†’  // Connection state
    38â†’  const [connectionState, setConnectionState] = useState<WebSocketState>(WebSocketState.CONNECTING);
    39â†’  const [error, setError] = useState<string | null>(null);
    40â†’
    41â†’  // Event streams
    42â†’  const [entityActivity, setEntityActivity] = useState<EntityActivityEvent[]>([]);
    43â†’  const [thresholdCrossings, setThresholdCrossings] = useState<ThresholdCrossingEvent[]>([]);
    44â†’  const [consciousnessState, setConsciousnessState] = useState<ConsciousnessStateEvent | null>(null);
    45â†’
    46â†’  // WebSocket reference (persists across renders)
    47â†’  const wsRef = useRef<WebSocket | null>(null);
    48â†’  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
    49â†’  const isIntentionalCloseRef = useRef(false);
    50â†’
    51â†’  /**
    52â†’   * Handle incoming WebSocket messages
    53â†’   */
    54â†’  const handleMessage = useCallback((event: MessageEvent) => {
    55â†’    try {
    56â†’      const data: WebSocketEvent = JSON.parse(event.data);
    57â†’
    58â†’      switch (data.type) {
    59â†’        case 'entity_activity':
    60â†’          setEntityActivity(prev => {
    61â†’            const updated = [...prev, data];
    62â†’            // Keep only the last N activities to prevent memory issues
    63â†’            return updated.slice(-MAX_ENTITY_ACTIVITIES);
    64â†’          });
    65â†’          break;
    66â†’
    67â†’        case 'threshold_crossing':
    68â†’          setThresholdCrossings(prev => {
    69â†’            const updated = [...prev, data];
    70â†’            // Keep only the last N crossings
    71â†’            return updated.slice(-MAX_THRESHOLD_CROSSINGS);
    72â†’          });
    73â†’          break;
    74â†’
    75â†’        case 'consciousness_state':
    76â†’          // Only keep the latest consciousness state
    77â†’          setConsciousnessState(data);
    78â†’          break;
    79â†’
    80â†’        default:
    81â†’          console.warn('[WebSocket] Unknown event type:', (data as any).type);
    82â†’      }
    83â†’    } catch (err) {
    84â†’      console.error('[WebSocket] Failed to parse message:', err);
    85â†’      setError('Failed to parse WebSocket message');
    86â†’    }
    87â†’  }, []);
    88â†’
    89â†’  /**
    90â†’   * Connect to WebSocket
    91â†’   */
    92â†’  const connect = useCallback(() => {
    93â†’    // Don't reconnect if intentionally closed
    94â†’    if (isIntentionalCloseRef.current) {
    95â†’      return;
    96â†’    }
    97â†’
    98â†’    try {
    99â†’      console.log('[WebSocket] Connecting to:', WS_URL);
   100â†’      setConnectionState(WebSocketState.CONNECTING);
   101â†’      setError(null);
   102â†’
   103â†’      const ws = new WebSocket(WS_URL);
   104â†’      wsRef.current = ws;
   105â†’
   106â†’      ws.onopen = () => {
   107â†’        console.log('[WebSocket] Connected');
   108â†’        setConnectionState(WebSocketState.CONNECTED);
   109â†’        setError(null);
   110â†’      };
   111â†’
   112â†’      ws.onmessage = handleMessage;
   113â†’
   114â†’      ws.onerror = (event) => {
   115â†’        // Don't set ERROR state - connection errors are expected during hot-reload
   116â†’        // Let onclose handle the reconnection flow
   117â†’        console.log('[WebSocket] Connection error (will auto-reconnect)');
   118â†’      };
   119â†’
   120â†’      ws.onclose = (event) => {
   121â†’        console.log('[WebSocket] Disconnected:', event.code, event.reason);
   122â†’
   123â†’        // Attempt reconnection if not intentionally closed
   124â†’        if (!isIntentionalCloseRef.current) {
   125â†’          setConnectionState(WebSocketState.RECONNECTING);
   126â†’          console.log(`[WebSocket] Reconnecting in ${RECONNECT_DELAY}ms...`);
   127â†’          reconnectTimeoutRef.current = setTimeout(() => {
   128â†’            connect();
   129â†’          }, RECONNECT_DELAY);
   130â†’        } else {
   131â†’          setConnectionState(WebSocketState.DISCONNECTED);
   132â†’        }
   133â†’      };
   134â†’    } catch (err) {
   135â†’      console.log('[WebSocket] Connection failed, will retry:', err);
   136â†’
   137â†’      // Attempt reconnection
   138â†’      if (!isIntentionalCloseRef.current) {
   139â†’        setConnectionState(WebSocketState.RECONNECTING);
   140â†’        reconnectTimeoutRef.current = setTimeout(() => {
   141â†’          connect();
   142â†’        }, RECONNECT_DELAY);
   143â†’      } else {
   144â†’        setConnectionState(WebSocketState.ERROR);
   145â†’        setError(err instanceof Error ? err.message : 'Unknown connection error');
   146â†’      }
   147â†’    }
   148â†’  }, [handleMessage]);
   149â†’
   150â†’  /**
   151â†’   * Disconnect WebSocket
   152â†’   */
   153â†’  const disconnect = useCallback(() => {
   154â†’    isIntentionalCloseRef.current = true;
   155â†’
   156â†’    // Clear reconnection timeout
   157â†’    if (reconnectTimeoutRef.current) {
   158â†’      clearTimeout(reconnectTimeoutRef.current);
   159â†’      reconnectTimeoutRef.current = null;
   160â†’    }
   161â†’
   162â†’    // Close WebSocket connection
   163â†’    if (wsRef.current) {
   164â†’      wsRef.current.close();
   165â†’      wsRef.current = null;
   166â†’    }
   167â†’
   168â†’    setConnectionState(WebSocketState.DISCONNECTED);
   169â†’  }, []);
   170â†’
   171â†’  /**
   172â†’   * Initialize WebSocket connection on mount
   173â†’   * Cleanup on unmount
   174â†’   *
   175â†’   * CRITICAL FIX: Empty dependency array prevents reconnection loop
   176â†’   * - connect/disconnect are stable callbacks (don't need in deps)
   177â†’   * - Including them causes effect to re-fire, creating multiple WS connections
   178â†’   * - Memory leak fixed by running effect only once on mount
   179â†’   */
   180â†’  useEffect(() => {
   181â†’    isIntentionalCloseRef.current = false;
   182â†’    connect();
   183â†’
   184â†’    // Cleanup on unmount
   185â†’    return () => {
   186â†’      disconnect();
   187â†’    };
   188â†’    // eslint-disable-next-line react-hooks/exhaustive-deps
   189â†’  }, []); // EMPTY DEPS - run once on mount, cleanup once on unmount
   190â†’
   191â†’  return {
   192â†’    entityActivity,
   193â†’    thresholdCrossings,
   194â†’    consciousnessState,
   195â†’    connectionState,
   196â†’    error
   197â†’  };
   198â†’}
