     1â†’/**
     2â†’ * PixiCanvas - React wrapper for PixiRenderer
     3â†’ *
     4â†’ * Strangler pattern: Drop-in replacement for GraphCanvas
     5â†’ * - Same props interface
     6â†’ * - Same event emissions (for tooltips/panels)
     7â†’ * - Better performance (60fps with 2500+ nodes)
     8â†’ */
     9â†’
    10â†’'use client';
    11â†’
    12â†’import { useEffect, useRef, useState } from 'react';
    13â†’import { PixiRenderer } from '../lib/renderer/PixiRenderer';
    14â†’import type { RendererAdapter, ViewModel } from '../lib/renderer/types';
    15â†’import type { Node, Link, Operation } from '../hooks/useGraphData';
    16â†’
    17â†’interface PixiCanvasProps {
    18â†’  nodes: Node[];
    19â†’  links: Link[];
    20â†’  operations: Operation[];
    21â†’  entities?: { entity_id: string; name?: string }[];
    22â†’  selectedEntity?: string;
    23â†’  workingMemory?: Set<string>;
    24â†’  linkFlows?: Map<string, number>;
    25â†’  recentFlips?: Array<{ node_id: string; direction: 'on' | 'off'; timestamp: number }>;
    26â†’}
    27â†’
    28â†’export function PixiCanvas({
    29â†’  nodes,
    30â†’  links,
    31â†’  operations,
    32â†’  entities = [],
    33â†’  selectedEntity = 'structural',
    34â†’  workingMemory,
    35â†’  linkFlows,
    36â†’  recentFlips,
    37â†’}: PixiCanvasProps) {
    38â†’  const containerRef = useRef<HTMLDivElement>(null);
    39â†’  const rendererRef = useRef<RendererAdapter | null>(null);
    40â†’  const [stats, setStats] = useState({ fps: 0, nodeCount: 0, linkCount: 0 });
    41â†’
    42â†’  // Initialize renderer on mount
    43â†’  useEffect(() => {
    44â†’    if (!containerRef.current) return;
    45â†’
    46â†’    const renderer = new PixiRenderer({
    47â†’      showStats: true,
    48â†’      enableCulling: false, // Will add in Phase 4
    49â†’      enableLOD: false,
    50â†’    });
    51â†’
    52â†’    rendererRef.current = renderer;
    53â†’    renderer.mount(containerRef.current);
    54â†’
    55â†’    return () => {
    56â†’      renderer.unmount();
    57â†’      rendererRef.current = null;
    58â†’    };
    59â†’  }, []);
    60â†’
    61â†’  // Update data when nodes/links change
    62â†’  useEffect(() => {
    63â†’    if (!rendererRef.current) return;
    64â†’
    65â†’    console.log('[PixiCanvas] Updating data - nodes:', nodes.length, 'links:', links.length);
    66â†’
    67â†’    const viewModel: ViewModel = {
    68â†’      nodes: nodes.map((n) => ({
    69â†’        id: n.id || n.node_id!,
    70â†’        name: n.text || (n as any).name || 'Unknown',
    71â†’        node_type: n.node_type || 'Unknown',
    72â†’        description: (n as any).description,
    73â†’        weight: n.weight,
    74â†’        energy: n.energy,
    75â†’        last_active: n.last_active,
    76â†’        last_traversal_time: n.last_traversal_time,
    77â†’        traversal_count: n.traversal_count,
    78â†’        entity_activations: n.entity_activations,
    79â†’        x: n.x,
    80â†’        y: n.y,
    81â†’      })),
    82â†’      links: links.map((l) => ({
    83â†’        id: l.id || `${l.source}-${l.target}`,
    84â†’        source: typeof l.source === 'object' ? l.source.id : l.source,
    85â†’        target: typeof l.target === 'object' ? l.target.id : l.target,
    86â†’        type: l.type,
    87â†’        strength: (l as any).strength,
    88â†’        valence: (l as any).valence,
    89â†’        confidence: (l as any).confidence,
    90â†’        created_at: (l as any).created_at,
    91â†’        last_traversal: (l as any).last_traversal,
    92â†’        traversal_count: (l as any).traversal_count,
    93â†’      })),
    94â†’      entities: entities.map((e) => ({
    95â†’        entity_id: e.entity_id,
    96â†’        name: e.name,
    97â†’      })),
    98â†’      operations: operations as any,
    99â†’      selectedEntity,
   100â†’      workingMemory,
   101â†’      linkFlows,
   102â†’      recentFlips,
   103â†’    };
   104â†’
   105â†’    rendererRef.current.setData(viewModel);
   106â†’
   107â†’    // Update stats display
   108â†’    const stats = rendererRef.current.getStats();
   109â†’    setStats({
   110â†’      fps: stats.fps,
   111â†’      nodeCount: stats.nodeCount,
   112â†’      linkCount: stats.linkCount,
   113â†’    });
   114â†’  }, [nodes, links, entities, selectedEntity]); // operations excluded - changes too frequently
   115â†’
   116â†’  // Handle window resize
   117â†’  useEffect(() => {
   118â†’    const handleResize = () => {
   119â†’      if (containerRef.current && rendererRef.current) {
   120â†’        const { clientWidth, clientHeight } = containerRef.current;
   121â†’        rendererRef.current.resize(clientWidth, clientHeight);
   122â†’      }
   123â†’    };
   124â†’
   125â†’    window.addEventListener('resize', handleResize);
   126â†’    return () => window.removeEventListener('resize', handleResize);
   127â†’  }, []);
   128â†’
   129â†’  // Handle hover/click events (emit same events as GraphCanvas for tooltip compatibility)
   130â†’  useEffect(() => {
   131â†’    const container = containerRef.current;
   132â†’    if (!container || !rendererRef.current) return;
   133â†’
   134â†’    const handlePointerMove = (e: PointerEvent) => {
   135â†’      if (!rendererRef.current) return;
   136â†’
   137â†’      const rect = container.getBoundingClientRect();
   138â†’      const x = e.clientX - rect.left;
   139â†’      const y = e.clientY - rect.top;
   140â†’
   141â†’      const result = rendererRef.current.pick(x, y);
   142â†’
   143â†’      if (result.type === 'node') {
   144â†’        const customEvent = new CustomEvent('node:hover', {
   145â†’          detail: { node: result.data, event: e },
   146â†’        });
   147â†’        window.dispatchEvent(customEvent);
   148â†’      } else if (result.type === 'link') {
   149â†’        const customEvent = new CustomEvent('link:hover', {
   150â†’          detail: { link: result.data, event: e },
   151â†’        });
   152â†’        window.dispatchEvent(customEvent);
   153â†’      } else {
   154â†’        window.dispatchEvent(new CustomEvent('node:leave'));
   155â†’        window.dispatchEvent(new CustomEvent('link:leave'));
   156â†’      }
   157â†’    };
   158â†’
   159â†’    const handleClick = (e: PointerEvent) => {
   160â†’      if (!rendererRef.current) return;
   161â†’
   162â†’      const rect = container.getBoundingClientRect();
   163â†’      const x = e.clientX - rect.left;
   164â†’      const y = e.clientY - rect.top;
   165â†’
   166â†’      const result = rendererRef.current.pick(x, y);
   167â†’
   168â†’      if (result.type === 'node') {
   169â†’        const customEvent = new CustomEvent('node:click', {
   170â†’          detail: { node: result.data, event: e },
   171â†’        });
   172â†’        window.dispatchEvent(customEvent);
   173â†’      }
   174â†’    };
   175â†’
   176â†’    container.addEventListener('pointermove', handlePointerMove);
   177â†’    container.addEventListener('click', handleClick);
   178â†’
   179â†’    return () => {
   180â†’      container.removeEventListener('pointermove', handlePointerMove);
   181â†’      container.removeEventListener('click', handleClick);
   182â†’    };
   183â†’  }, []);
   184â†’
   185â†’  return (
   186â†’    <div className="relative w-full h-full">
   187â†’      {/* PixiJS canvas container */}
   188â†’      <div ref={containerRef} className="w-full h-full" />
   189â†’
   190â†’      {/* Stats overlay (debugging) */}
   191â†’      {stats.fps > 0 && (
   192â†’        <div className="absolute top-4 left-4 bg-black/50 text-white text-xs p-2 rounded font-mono">
   193â†’          <div>FPS: {stats.fps}</div>
   194â†’          <div>Nodes: {stats.nodeCount}</div>
   195â†’          <div>Links: {stats.linkCount}</div>
   196â†’          <div className="text-cyan-400 mt-1">âš¡ WebGL</div>
   197â†’        </div>
   198â†’      )}
   199â†’    </div>
   200â†’  );
   201â†’}
   202â†’

