   585â†’function getNodeSize(node: Node): number {
   586â†’  // Use computed weight from node or fallback to calculation
   587â†’  const weight = node.weight || computeNodeWeight(node);
   588â†’  // Expanded range: 20px (min) to 48px (max weight)
