   592â†’function computeNodeWeight(node: Node): number {
   593â†’  const energy = node.energy || 0;
   594â†’  const confidence = node.confidence || 0.5;
   595â†’  const traversalCount = node.traversal_count || 0;
   596â†’  const normalizedTraversals = Math.min(1.0, Math.log10(traversalCount + 1) / 2);
   597â†’  return (energy * 0.4) + (confidence * 0.3) + (normalizedTraversals * 0.3);
   598â†’}
