   600â†’function getNodeGlow(node: Node): string {
   601â†’  const now = Date.now();
   602â†’
   603â†’  // Priority: Show only the most important glow (performance optimization)
   604â†’
   605â†’  // 1. NEWEST CREATED (last 5 minutes) - Bright cyan pulse (highest priority)
   606â†’  if (node.created_at) {
   607â†’    const age = now - node.created_at;
   608â†’    if (age < 300000) { // 5 minutes
   609â†’      const intensity = 1 - (age / 300000);
   610â†’      const glowSize = 12 + (intensity * 16);
   611â†’      return `drop-shadow(0 0 ${glowSize}px rgba(45, 212, 191, ${intensity})) drop-shadow(0 0 ${glowSize/2}px rgba(45, 212, 191, ${intensity}))`;
   612â†’    }
   613â†’  }
   614â†’
   615â†’  // 2. RECENTLY REINFORCED/DE-REINFORCED (last 2 minutes)
   616â†’  if (node.last_modified && node.reinforcement_weight !== undefined) {
   617â†’    const age = now - node.last_modified;
   618â†’    if (age < 120000) { // 2 minutes
   619â†’      const intensity = 1 - (age / 120000);
   620â†’      const glowSize = 6 + (intensity * 8);
   621â†’
   622â†’      if (node.reinforcement_weight > 0.05) {
   623â†’        // GREEN for positive reinforcement
   624â†’        return `drop-shadow(0 0 ${glowSize}px rgba(34, 197, 94, ${intensity * 0.8}))`;
   625â†’      } else if (node.reinforcement_weight < -0.05) {
   626â†’        // RED for de-reinforcement
   627â†’        return `drop-shadow(0 0 ${glowSize}px rgba(239, 68, 68, ${intensity * 0.8}))`;
   628â†’      }
   629â†’    }
   630â†’  }
   631â†’
   632â†’  // 3. RECENTLY TRAVERSED (last 2 minutes) - Yellow/green (lowest priority)
   633â†’  if (node.last_active) {
   634â†’    const age = now - node.last_active;
   635â†’    if (age < 120000) { // 2 minutes
   636â†’      const intensity = 1 - (age / 120000);
   637â†’      const glowSize = 5 + (intensity * 6);
   638â†’      return `drop-shadow(0 0 ${glowSize}px rgba(94, 252, 130, ${intensity * 0.6}))`;
   639â†’    }
   640â†’  }
   641â†’
   642â†’  return 'none';
   643â†’}
