   645â†’/**
   646â†’ * Gold shimmer indicates high consciousness energy (content signal)
   647â†’ * Strategic Gold Rule: Use ONLY for content signals (energy/activity)
   648â†’ */
   649â†’function shouldApplyGoldShimmer(node: Node): boolean {
   650â†’  // High energy nodes (active consciousness)
   651â†’  const energy = node.energy || 0;
   652â†’  if (energy > 0.7) return true;
   653â†’
   654â†’  // High traversal activity (frequently explored)
   655â†’  const traversalCount = node.traversal_count || 0;
   656â†’  if (traversalCount > 10) return true;
   657â†’
   658â†’  // Recently very active (last 5 minutes)
   659â†’  if (node.last_active) {
   660â†’    const age = Date.now() - node.last_active;
   661â†’    if (age < 300000 && energy > 0.5) { // 5 minutes with moderate energy
   662â†’      return true;
   663â†’    }
   664â†’  }
   665â†’
   666â†’  return false;
   667â†’}
