   691â†’function getValenceColor(valence: number | undefined): string {
   692â†’  if (valence === null || valence === undefined) return '#64748b';
   693â†’
   694â†’  const normalized = (valence + 1.0) / 2.0;
   695â†’
   696â†’  if (normalized < 0.5) {
   697â†’    const t = normalized * 2;
   698â†’    return d3.interpolateRgb('#ef4444', '#94a3b8')(t); // Red to gray
   699â†’  } else {
   700â†’    const t = (normalized - 0.5) * 2;
   701â†’    return d3.interpolateRgb('#94a3b8', '#22c55e')(t); // Gray to green
   702â†’  }
   703â†’}
