   722â†’// ============================================================================
   723â†’// Temporal Force (Timeline Layout)
   724â†’// ============================================================================
   725â†’
   726â†’function forceTemporalX(width: number) {
   727â†’  let nodes: Node[];
   728â†’
   729â†’  function force(alpha: number) {
   730â†’    if (!nodes) return;
   731â†’
   732â†’    const now = Date.now();
   733â†’    // Find oldest and newest timestamps
   734â†’    const timestamps = nodes
   735â†’      .map(n => n.last_active || n.created_at || 0)
   736â†’      .filter(t => t > 0);
   737â†’
   738â†’    if (timestamps.length === 0) return;
   739â†’
   740â†’    const minTime = Math.min(...timestamps);
   741â†’    const maxTime = Math.max(...timestamps);
   742â†’    const timeRange = maxTime - minTime;
   743â†’
   744â†’    // FALLBACK: If timestamps are uniform (seed data), disable temporal force
   745â†’    // Range less than 1 hour = likely bulk-imported seed data
   746â†’    if (timeRange < 3600000) {
   747â†’      // console.log('[Temporal Force] Timestamps too uniform, skipping');
   748â†’      return; // Let other forces handle layout
   749â†’    }
   750â†’
   751â†’    // Pull nodes left-to-right based on temporal position
   752â†’    nodes.forEach((node: any) => {
   753â†’      const nodeTime = node.last_active || node.created_at || minTime;
   754â†’      const timePos = (nodeTime - minTime) / timeRange; // 0 (old) to 1 (new)
   755â†’
   756â†’      // Target X position: old on left (20% width), new on right (80% width)
   757â†’      const targetX = width * 0.2 + (timePos * width * 0.6);
   758â†’
   759â†’      // Apply gentle force toward target position
   760â†’      const dx = targetX - node.x;
   761â†’      node.vx += dx * alpha * 0.15; // Gentle 15% strength
   762â†’    });
   763â†’  }
   764â†’
   765â†’  force.initialize = function(_: any) {
   766â†’    nodes = _;
   767â†’  };
   768â†’
   769â†’  return force;
   770â†’}
