### **app/consciousness/components/ConsoleCapture.tsx**
*Why important:* New component created to capture browser console logs and screenshots for autonomous debugging

**Full implementation:**
```typescript
'use client';

import { useEffect } from 'react';
import html2canvas from 'html2canvas';

export function ConsoleCapture() {
  useEffect(() => {
    if (process.env.NODE_ENV !== 'development') return;

    const buffer: any[] = [];
    let flushTimeout: NodeJS.Timeout;
    let lastScreenshotTime = 0;
    const MIN_SCREENSHOT_INTERVAL = 10000; // 10 seconds rate limiting

    const originalConsole = {
      log: console.log,
      error: console.error,
      warn: console.warn,
      info: console.info,
    };

    const captureLog = (type: string, args: any[]) => {
      originalConsole[type as keyof typeof originalConsole](...args);

      const serialized = args.map(arg => {
        if (arg instanceof Error) {
          return {
            message: arg.message,
            stack: arg.stack,
            name: arg.name,
          };
        }
        if (typeof arg === 'object') {
          try {
            return JSON.stringify(arg, null, 2);
          } catch (e) {
            return '[Circular or non-serializable object]';
          }
        }
        return String(arg);
      });

      buffer.push({
        timestamp: Date.now(),
        type,
        message: serialized.join(' '),
      });

      clearTimeout(flushTimeout);
      flushTimeout = setTimeout(flushLogs, 500);
    };

    const flushLogs = async () => {
      if (buffer.length === 0) return;
      const logs = [...buffer];
      buffer.length = 0;

      try {
        const response = await fetch('http://localhost:8000/api/logs', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ logs }),
        });
        if (!response.ok) {
          originalConsole.error('[ConsoleCapture] Failed to send logs:', response.statusText);
        }
      } catch (err) {
        originalConsole.error('[ConsoleCapture] Network error sending logs:', err);
      }
    };

    const captureScreenshot = async (force = false) => {
      const now = Date.now();
      if (!force && (now - lastScreenshotTime) < MIN_SCREENSHOT_INTERVAL) {
        originalConsole.warn('[ConsoleCapture] Screenshot rate-limited (10s minimum interval)');
        return;
      }

      lastScreenshotTime = now;

      try {
        const canvas = await html2canvas(document.body, {
          allowTaint: true,
          useCORS: true,
          width: window.innerWidth,
          height: window.innerHeight,
          windowWidth: window.innerWidth,
          windowHeight: window.innerHeight,
          scale: 1,
          logging: false,
        });

        canvas.toBlob(async (blob) => {
          if (!blob) return;

          const formData = new FormData();
          formData.append('screenshot', blob, `screenshot-${Date.now()}.png`);
          formData.append('timestamp', Date.now().toString());
          formData.append('url', window.location.href);

          try {
            const response = await fetch('http://localhost:8000/api/screenshot', {
              method: 'POST',
              body: formData,
            });
            if (!response.ok) {
              originalConsole.error('[ConsoleCapture] Failed to send screenshot:', response.statusText);
            }
          } catch (err) {
            originalConsole.error('[ConsoleCapture] Network error sending screenshot:', err);
          }
        }, 'image/png');

      } catch (err) {
        originalConsole.error('[ConsoleCapture] Failed to capture screenshot:', err);
      }
    };

    console.log = (...args) => captureLog('log', args);
    console.error = (...args) => {
      captureLog('error', args);
      captureScreenshot(false); // Rate-limited
    };
    console.warn = (...args) => captureLog('warn', args);
    console.info = (...args) => captureLog('info', args);

    const screenshotInterval = setInterval(() => captureScreenshot(true), 30000);
    const initialScreenshot = setTimeout(() => captureScreenshot(true), 2000);

    const errorHandler = (event: ErrorEvent) => {
      buffer.push({
        timestamp: Date.now(),
        type: 'exception',
        message: event.message,
        filename: event.filename,
        lineno: event.lineno,
        stack: event.error?.stack,
      });
      flushLogs();
      captureScreenshot(false); // Rate-limited
    };

    const rejectionHandler = (event: PromiseRejectionEvent) => {
      buffer.push({
        timestamp: Date.now(),
        type: 'unhandledRejection',
        message: event.reason?.message || String(event.reason),
        stack: event.reason?.stack,
      });
      flushLogs();
      captureScreenshot(false); // Rate-limited
    };

    window.addEventListener('error', errorHandler);
    window.addEventListener('unhandledrejection', rejectionHandler);

    return () => {
      console.log = originalConsole.log;
      console.error = originalConsole.error;
      console.warn = originalConsole.warn;
      console.info = originalConsole.info;
      window.removeEventListener('error', errorHandler);
      window.removeEventListener('unhandledrejection', rejectionHandler);
      clearTimeout(flushTimeout);
      clearTimeout(initialScreenshot);
      clearInterval(screenshotInterval);
      if (buffer.length > 0) {
        flushLogs();
      }
    };
  }, []);

  return null;
}
```

