// Screenshot capture function with rate limiting
const captureScreenshot = async (force = false) => {
  // Rate limit: Only capture if enough time has passed since last screenshot
  // (unless force=true for periodic captures)
  const now = Date.now();
  if (!force && (now - lastScreenshotTime) < MIN_SCREENSHOT_INTERVAL) {
    originalConsole.warn('[ConsoleCapture] Screenshot rate-limited (10s minimum interval)');
    return;
  }

  lastScreenshotTime = now;

  try {
    // Helper to convert SVG to canvas in cloned document
    const convertSVGToCanvas = (clonedDoc: Document) => {
      const svgElements = clonedDoc.querySelectorAll('svg');
      svgElements.forEach((svg) => {
        try {
          const bbox = (svg as SVGSVGElement).getBBox();
          const canvas = clonedDoc.createElement('canvas');
          const ctx = canvas.getContext('2d');
          if (!ctx) return;

          // Set canvas size to match SVG
          canvas.width = bbox.width || parseInt((svg as SVGSVGElement).getAttribute('width') || '800');
          canvas.height = bbox.height || parseInt((svg as SVGSVGElement).getAttribute('height') || '600');
          canvas.style.cssText = (svg as HTMLElement).style.cssText;

          // Serialize SVG to data URL
          const svgData = new XMLSerializer().serializeToString(svg);
          const svgBlob = new Blob([svgData], { type: 'image/svg+xml;charset=utf-8' });
          const url = URL.createObjectURL(svgBlob);

          // Draw SVG onto canvas
          const img = new Image();
          img.onload = () => {
            ctx.drawImage(img, 0, 0);
            URL.revokeObjectURL(url);
          };
          img.src = url;

          // Replace SVG with canvas in cloned document
          svg.parentNode?.replaceChild(canvas, svg);
        } catch (e) {
          originalConsole.warn('[ConsoleCapture] Failed to convert SVG to canvas:', e);
        }
      });
    };

    const canvas = await html2canvas(document.body, {
      allowTaint: true,
      useCORS: true,
      width: window.innerWidth,
      height: window.innerHeight,
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight,
      scale: 1, // Reduce scale for performance
      logging: false, // Don't spam console with html2canvas logs
      onclone: (clonedDoc) => {
        convertSVGToCanvas(clonedDoc);
      }
    });

