// Screenshot capture function with rate limiting
const captureScreenshot = async (force = false) => {
  // Rate limit: Only capture if enough time has passed since last screenshot
  // (unless force=true for periodic captures)
  const now = Date.now();
  if (!force && (now - lastScreenshotTime) < MIN_SCREENSHOT_INTERVAL) {
    originalConsole.warn('[ConsoleCapture] Screenshot rate-limited (10s minimum interval)');
    return;
  }

  lastScreenshotTime = now;

  try {
    // Convert SVG to canvas BEFORE html2canvas runs
    // html2canvas can't render SVG, but it CAN render canvas elements
    const svgElements = document.querySelectorAll('svg');
    const replacements: { svg: SVGSVGElement; canvas: HTMLCanvasElement; parent: ParentNode }[] = [];

    for (const svgEl of Array.from(svgElements)) {
      try {
        const svg = svgEl as SVGSVGElement;
        const bbox = svg.getBoundingClientRect();

        // Create canvas with same dimensions
        const canvas = document.createElement('canvas');
        canvas.width = bbox.width;
        canvas.height = bbox.height;
        canvas.style.cssText = window.getComputedStyle(svg).cssText;
        canvas.style.width = bbox.width + 'px';
        canvas.style.height = bbox.height + 'px';

        const ctx = canvas.getContext('2d');
        if (!ctx) continue;

        // Serialize SVG to image
        const svgData = new XMLSerializer().serializeToString(svg);
        const svgBlob = new Blob([svgData], { type: 'image/svg+xml;charset=utf-8' });
        const url = URL.createObjectURL(svgBlob);

        // Draw SVG as image on canvas (synchronously via Image)
        await new Promise<void>((resolve) => {
          const img = new Image();
          img.onload = () => {
            ctx.drawImage(img, 0, 0, bbox.width, bbox.height);
            URL.revokeObjectURL(url);
            resolve();
          };
          img.onerror = () => {
            URL.revokeObjectURL(url);
            resolve(); // Continue even if this one fails
          };
          img.src = url;
        });

        // Replace SVG with canvas temporarily
        if (svg.parentNode) {
          svg.parentNode.replaceChild(canvas, svg);
          replacements.push({ svg, canvas, parent: canvas.parentNode! });
        }
      } catch (e) {
        originalConsole.warn('[ConsoleCapture] Failed to convert SVG:', e);
      }
    }

    // Now capture with html2canvas (will capture the canvas elements)
    const canvas = await html2canvas(document.body, {
      allowTaint: true,
      useCORS: true,
      width: window.innerWidth,
      height: window.innerHeight,
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight,
      scale: 1,
      logging: false,
    });

    // Restore original SVG elements
    replacements.forEach(({ svg, canvas, parent }) => {
      try {
        parent.replaceChild(svg, canvas);
      } catch (e) {
        originalConsole.warn('[ConsoleCapture] Failed to restore SVG:', e);
      }
    });

